import React, { Component } from "react";
import {
  Card,
  CardBody,
  CardImg,
  CardTitle,
  CardText,
  CardImgOverlay
} from "reactstrap";

export default class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDish: null
    };
  }
  onSelectDish = dish => {
    this.setState({
      selectedDish: dish
    });
  };

  renderDish = dish => {
    if (dish != null) {
      return (
        <div className="col-12 mt-1">
          <Card>
            <CardImg width="100%" src={dish.image} alt={dish.name} />
            <CardBody>
              <CardTitle>{dish.name}</CardTitle>
              <CardText>{dish.description}</CardText>
            </CardBody>
          </Card>
        </div>
      );
    } else {
      return <div />;
    }
  };

  // renderComment = comment => {
  //   if (comment != null) {
  //     return (
  //       <div>
  //         <ul className="list-unstyled">
  //           <li>comment.comment</li>
  //         </ul>
  //       </div>
  //     );
  //   } else {
  //     return <div />;
  //   }
  // };
  render() {
    const menu = this.props.dishes.map(dish => {
      return (
        <div key={dish.id} className="col-12 col-md-5 mt-1">
          <Card onClick={() => this.onSelectDish(dish)}>
            <CardImg width="100%" src={dish.image} alt={dish.name} />
            <CardImgOverlay>
              <CardTitle>{dish.name}</CardTitle>
            </CardImgOverlay>
          </Card>
        </div>
      );
    });

    const showComments = this.props.dishes.map(comment => {
      return (
        <div key={comment.id}>
          <ul className="list-unstyled">
            <li>
              {comment.comments.map(commentE => {
                return commentE.comment;
              })}
            </li>
          </ul>
        </div>
      );
    });
    return (
      <div className="container">
        <div className="row">{menu}</div>
        <div className="row">
          <div className="col-6">
            {this.renderDish(this.state.selectedDish)}
          </div>
          <div >

          </div>
          <div className="col-6">
          <h1>Comment</h1>
  //         <ul className="list-unstyled">
  //           <li>comment.comment</li>
  //         </ul>
  //       </div>
        </div>
      </div>
    );
  }
}
