import React, { Component } from "react";
import { DISHES } from "../shared/Dishes";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardText,
  CardImgOverlay
} from "reactstrap";

export default class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dish: DISHES,
      selectedDish: null
    };
  }
  onSelectedDish = (dish) => {
    this.setState({
      selectedDish : dish
    })
  }
  renderSelectedDish = (dish) => {
    if(dish !== null){
      return (
        <React.Fragment>
          <div className="col-md-6">
          <Card>
            <CardImg src={dish.image} alt={dish.name} />
            <CardBody>
              <CardTitle>{dish.name}</CardTitle>
              <CardText>{dish.description}</CardText>
            </CardBody>
          </Card>
          </div>
          <div className="col-md-6">
            <h1>Comment</h1>
            <div className=""></div>
          </div>

        </React.Fragment>
      )
    } else {
      return <div/>
    }

  }
  render() {
    const menu = this.state.dish.map(item => {
      return (
        <div key={item.id} className="col-md-5 m-1">
          <Card onClick={()=>this.onSelectedDish(item)}>
            <CardImg src={item.image} alt={item.name} />
            <CardImgOverlay>
              <CardTitle>{item.name}</CardTitle>
            </CardImgOverlay>
          </Card>
        </div>
      );
    });
    return (
      <div className="container">
        <div className="row">{menu}</div>
        <div className="row">{this.renderSelectedDish(this.state.selectedDish)}</div>
      </div>
    );
  }
}
