import React, { Component } from 'react'


export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: ''
    };

  }

  handleChange = (e) => {
    this.setState({
      input : e.target.value
    })
  }
  render() {
    return (
      <div>
        <input onChange={this.handleChange} value={this.state.input}/>
        <button onClick={this.handleSubmit}>Submit</button>
      </div>
    )
  }
}
