import React, { Component } from "react";
import { DISHES } from "../shared/Dishes";
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardText,
  CardImgOverlay
} from "reactstrap";

export default class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dish: DISHES
    };
  }
  onSelectedDish = () => {
    this.setState({
      selectedDish: this.state.dish
    })
  }
  renderSelectedDish = (selectedDish) => {
    if(selectedDish !== null){
      return (
        <div>
          <Card>
            <CardImg src={selectedDish.image} alt={item.name} />
            <CardBody>
              <CardTitle>{item.name}</CardTitle>
              <CardText>{item.description}</CardText>
            </CardBody>
          </Card>
        </div>
      )
    } else {
      return <div/>
    }

  }
  render() {
    const menu = this.state.dish.map(item => {
      return (
        <div key={item.id} className="col-md-5 m-1">
          <Card onClick={this.onSelectedDish(item)}>
            <CardImg src={item.image} alt={item.name} />
            <CardImgOverlay>
              <CardTitle>{item.name}</CardTitle>
            </CardImgOverlay>
          </Card>
        </div>
      );
    });
    return (
      <div className="container">
        <div className="row">{menu}</div>
        <div className="row">{this.renderSelectedDish}</div>
      </div>
    );
  }
}
