import React, { Component } from "react";
import {
  Card,
  CardBody,
  CardImg,
  CardTitle,
  CardText,
  CardImgOverlay
} from "reactstrap";
import DishDetail from "./DishDetail";

export default class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDish: null
    };
  }
  onSelectDish = dish => {
    this.setState({
      selectedDish: dish
    });
  };

  renderDish = dish => {
    if (dish != null) {
      return (
        <Card>
          <CardImg width="100%" src={dish.image} alt={dish.name} />
          <CardBody>
            <CardTitle>{dish.name}</CardTitle>
            <CardText>{dish.description}</CardText>
          </CardBody>
        </Card>
      );
    } else {
      return <div />;
    }
  };

  renderComment = comment => {
    if (comment != null) {
      return (
        <div>
          <ul className="list-unstyled">
            <li>{comment.comment}</li>
            <li>{comment.author}</li>
          </ul>
        </div>
      );
    } else {
      return <div />;
    }
  };
  render() {
    const menu = this.props.dishes.map(dish => {
      return (
        <div key={dish.id} className="col-12 col-md-5 mt-1">
          <Card onClick={() => this.onSelectDish(dish)}>
            <CardImg width="100%" src={dish.image} alt={dish.name} />
            <CardImgOverlay>
              <CardTitle>{dish.name}</CardTitle>
            </CardImgOverlay>
          </Card>
        </div>
      );
    });

    const showComments = this.props.dishes.map(comment => {
      return (
        // <div key={comment.id}>
        //   <ul className="list-unstyled">
        //     <li>
        //       {comment.comments.map(commentE => {
        //         return commentE.comment;
        //       })}
        //     </li>
        //   </ul>
        // </div>

        <div key={comment.id} onClick={() => this.onSelectDish(comment)}>
          {comment.comments.map(commentE => {
            return (
              <ul className="list-unstyled">
                <li>{commentE.comment}</li>
              </ul>
            );
          })}
        </div>
      );
    });
    return (
      <div className="container">
        <div className="row">{menu}</div>
        <div className="row"><DishDetail/></div>
      </div>
    );
  }
}
